mapper.insert:
INSERT INTO
	COLLECTION
		(
		 COLLECTION_NO
		,MEMBER_NO
		,ORIGIN_NAME
		,COLLECTION_CONTENT
		,CHANGE_NAME
		,COLLECTION_TITLE
		)
	VALUES
		(
		 SEQ_CLNO.NEXTVAL
		,#{ memberNo }
		,#{ originName }
		,#{ collectionContent }
		,#{ changeName }
		,#{ collectionTitle }
		)

insert.foreach:
        	INTO
            	COLLECTION_MOVIE
					(
					 COLLECTION_NO
					,MOVIE_TITLE
					,MOVIE_YEAR
					)
				VALUES 
					(
					 SEQ_CLNO.CURRVAL
					,#{ item.movieTitle }
					,#{ item.movieYear }
					)

mapper.insert:
INSERT INTO
	COLLECTION_REPLY
		(
		 CO_REPLY_NO
		,CO_REPLY_CONTENT
		,MEMBER_NO
		,COLLECTION_NO
		)
	VALUES
		(
		 SEQ_CLRNO.NEXTVAL
		,#{ coReplyContent }
		,#{ memberNo }
		,#{ collectionNo }
		)

mapper.update:
UPDATE
	COLLECTION_REPLY
SET
	CO_REPLY_CONTENT = #{ coReplyContent }
WHERE
	CO_REPLY_NO = #{ coReplyNo }
	AND MEMBER_NO = #{ memberNo }

UPDATE
	COLLECTION_LIKE
SET
	COLLECTION_LIKE = #{ collectionLike }
WHERE
	COLLECTION_NO = #{ collectionNo }
	AND MEMBER_NO = #{ memberNo }

mapper.insert:
INSERT INTO
	COLLECTION_LIKE
	(
	 COLLECTION_LIKE
	,COLLECTION_NO
	,MEMBER_NO
	)
	VALUES
	(
	 1
	,#{ collectionNo }
	,#{ memberNo }
	)
