mapper.insert:
INSERT INTO
	COLLECTION
		(
		 COLLECTION_NO
		,MEMBER_NO
		,ORIGIN_NAME
		,COLLECTION_CONTENT
		,CHANGE_NAME
		,COLLECTION_TITLE
		)
	VALUES
		(
		 SEQ_CLNO.NEXTVAL
		,#{ memberNo }
		,#{ originName }
		,#{ collectionContent }
		,#{ changeName }
		,#{ collectionTitle }
		)

insert.foreach:
        	INTO
            	COLLECTION_MOVIE
					(
					 COLLECTION_NO
					,MOVIE_TITLE
					,MOVIE_YEAR
					)
				VALUES 
					(
					 SEQ_CLNO.CURRVAL
					,#{ item.movieTitle }
					,#{ item.movieYear }
					)
