mapper.update:
UPDATE
	  MOVIE_RATING
   SET
      RATING=#{rating}
 WHERE
      MOVIE_TITLE=#{movieTitle}
   AND
      MOVIE_YEAR=#{movieYear}
   AND
      MEMBER_NO=#{memberNo}

UPDATE
      COMMENT_LIKE
  SET
      COMMENT_LIKE = 'N',
      COMMENT_DISLIKE = #{disLike}
WHERE
 	 COMMENT_NO=#{commentNo}
  AND
     COMMENT_WRITER=#{commentWriter}

UPDATE
	  COMMENTS
   SET
      COMMENT_CONTENT =#{commentContent},
      COMMENT_ENROLLDATE = SYSDATE,
      SPOILER = #{spoiler}
 WHERE
      MOVIE_TITLE = #{movieTitle}
   AND
      MOVIE_YEAR = #{movieYear}
   AND
      MEMBER_NO = #{memberNo}

UPDATE 
      COMMENTS
   SET
      STATUS = 'N'
  WHERE
      MOVIE_TITLE = #{movieTitle}
   AND
      MOVIE_YEAR = #{movieYear}
   AND
      MEMBER_NO = #{memberNo}
