mapper.insert:
INSERT INTO
	MEETING
		(
		 MEETING_NO
		,MEETING_TITLE
		,MEETING_EXP
		,MEETING_PLACE
		,MEETING_PLACE_DETAIL
		,MOVIE_TITLE
		,MOVIE_YEAR
		,MEMBER_NO
		)
	VALUES
		(
		 SEQ_METNO.NEXTVAL
		,#{ meetingTitle }
		,#{ meetingExp }
		,#{ meetingPlace }
		,#{ meetingPlaceDetail }
		,#{ movieTitle }
		,#{ movieYear }
		,#{ memberNo }
		)

INSERT INTO
	MEETING_USER
		(
		 MEETING_NO
		,MEMBER_NO
		,MEETING_ACCEPT
		,MEETING_CONTENT
		)
	VALUES
		(
		 SEQ_METNO.CURRVAL
		,#{ memberNo }
		,#{ meetingAccept }
		,#{ meetingContent }
		)

INSERT INTO
	MEETING_USER
		(
		 MEETING_NO
		,MEMBER_NO
		,MEETING_ACCEPT
		,MEETING_CONTENT
		)
	VALUES
		(
		 #{ meetingNo }
		,#{ memberNo }
		,'N'
		,#{ meetingContent }
		)
